<template>
  <b-card 
    no-body 
    class="mb-1">
    <b-card-header
      v-b-toggle="collapseId"
      role="tab">
      <h5 
        class="card-title" 
        v-text="title" />
    </b-card-header>
    <b-collapse 
      :id="collapseId" 
      visible 
      accordion="settings" 
      role="tabpanel"
      @show="emit('show')"
      @shown="emit('shown')"
      @hide="emit('hide')"
      @hidden="emit('hidden')">
      <b-card-body>
        <slot />
      </b-card-body>
    </b-collapse>
  </b-card>
</template>

<script>
export default {
  props: {
    id: {
      type: String,
      required: true
    },
    title: {
      type: String,
      required: true
    }
  },
  methods: {
    emit(event) {
      this.$emit(event, this.collapseId)
    }
  },
  computed: {
    collapseId() {
      return `settings-${this.id}`
    }
  }
}
</script>
